import React from 'react';
import "../styles/search.css";
import { useState } from 'react';


function game({game}){
  return(
    <div class="boxgame">

    </div>
  );
}
function profil({profil}){
  return(<div class="boxgame">
      
    </div>);
}

function searchlist({person},{games}){
  const filtperson=person.map(person =>  <Card key={person.id} person={person} />);
  const filtgame=games.map(person =>  <Card key={person.id} person={person} />);
  return(
    <div>
      <p>Games</p>
      {filtgame}
      <p>Profils</p>
      {filtperson}
    </div>
  )
}

export default function Search() {
    const [value, setValue] = useState("");
    const [suggestions, setSuggestions] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
          try {
            const { data } = await axios.get(
              `https://dummyjson.com/products/search?q=${value}`
            );
    
            setSuggestions(data.products);
          } catch (error) {
            console.log(error);
          }
        };
    
        fetchData();
      }, [value]);
    
    return (
    <div id="search">
        <div class="navbar">
            <span>Home</span>
            <span>Recherche :</span>
        </div>

        <div class="searchbar">
            <h1>Recherche</h1>
            <input type="text" 
              placeholder="Search.."
              value={value}
              onChange={(e) => {
                setValue(e.target.value);
              }
            }/>
            </div>
        </div>
    )
}


function SearchBar() {
  const [search, setSearch] = useState("");
  const items = ["Apple", "Banana", "Cherry", "Orange", "Mango"];

  const filteredItems = items.filter((item) =>
    item.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <input
        type="text"
        placeholder="Rechercher un fruit..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{
          width: "300px",
          padding: "10px",
          fontSize: "16px",
          border: "2px solid #ccc",
          borderRadius: "5px",
          outline: "none"
        }}
      />
      <ul style={{ listStyle: "none", padding: 0, marginTop: "20px" }}>
        {filteredItems.map((item, index) => (
          <li key={index} style={{ fontSize: "18px", margin: "5px 0" }}>
            {item}
          </li>
        ))}
      </ul>
    </div>
  );
}
