import React from 'react';
import "../styles/search.css";
import { useState } from 'react';


function Cardgame({game}){
  return(
    <div class="boxgame">
      <div>
        <h2>{game.name}</h2>
        <p>{game.email}</p>
      </div>
    </div>
  );
}
function Cardprofil({profil}){
  return(
  <div class="boxgame">
      <div>
        <h2>{profil.name}</h2>
        <p>{profil.email}</p>
      </div>
    </div>);
}

function Searchlist({person},{games}){
  const filtperson=person.map(game =>  <Cardgame key={game.id} person={game} />);
  const filtgame=games.map(profil =>  <Cardprofil key={profil.id} person={profil} />);
  return(
    <div>
      <p>Games</p>
      {filtgame}
      <p>Profils</p>
      {filtperson}
    </div>
  )
}

const Scroll = (props) => {
  return( 
    <div style={{overflowY: 'scroll', height:'70vh'}}>
      {props.children}
    </div>	
  );
}

export default function Search({datagame,dataprofil}) {
    const [value, setValue] = useState("");
    ///const [suggestions, setSuggestions] = useState([]);
    ///const items = ["Apple", "Banana", "Cherry", "Orange", "Mango"];
    const filteredgame = datagame.filter((item) =>
      item.name.toLowerCase().includes(value.toLowerCase())
    );
    const filteredprofil = dataprofil.filter((item) =>
      item.name.toLowerCase().includes(value.toLowerCase())
    );
/*
    useEffect(() => {
        const fetchData = async () => {
          try {
            const { data } = await axios.get(
              `https://dummyjson.com/products/search?q=${value}`
            );
    
            setSuggestions(data.products);
          } catch (error) {
            console.log(error);
          }
        };
    
        fetchData();
      }, [value]);
    */
    return (
    <div id="search">
        <div class="navbar">
            <span>Home</span>
            <span>Recherche :</span>
        </div>

        <div class="searchbar">
            <h1>Recherche</h1>
            <input type="text" 
              placeholder="Search.."
              value={value}
              onChange={(e) => {
                setValue(e.target.value);
              }}/>
            <div>
              <Searchlist person={filteredprofil} game={filteredgame}/>
            </div>
        </div>
    </div>
    )
}











