import React from 'react';
import "../styles/search.css";
import { useState } from 'react';


function Cardgame({game}){
  return(
    <div class="boxgame">
      <div>
        <h2>{game.name}</h2>
        <p>{game.genre}</p>
      </div>
    </div>
  );
}
function Cardprofil({profil}){
  return(
  <div class="boxgame">
      <div>
        <h2>{profil.name}</h2>
        <p>{profil.email}</p>
      </div>
    </div>);
}

function Searchlist({ person = [], games = [] }){
  const filtgame=games.map(game =>  <Cardgame key={game.id} game={game} />);
  const filtperson=person.map(profil =>  <Cardprofil key={profil.id} profil={profil} />);
  return(
    <div>
      <h3>ðŸŽ® Games</h3>
      {filtgame}
      <h3>ðŸ‘¤ Profils</h3>
      {filtperson}
    </div>
  )
}

const Scroll = (props) => {
  return( 
    <div style={{overflowY: 'scroll', height:'70vh'}}>
      {props.children}
    </div>	
  );
}

export default function Search({datagame,dataprofil}) {
    const [value, setValue] = useState("");
    const [searchShow, setSearchShow] = useState(false);
    ///const [suggestions, setSuggestions] = useState([]);
    const filteredgame = datagame.filter((item) =>
      item.name.toLowerCase().includes(value.toLowerCase())
    );
    const filteredprofil = dataprofil.filter((item) =>
      item.name.toLowerCase().includes(value.toLowerCase())
    );
/*
    useEffect(() => {
        const fetchData = async () => {
          try {
            const { data } = await axios.get(
              `https://dummyjson.com/products/search?q=${value}`
            );
    
            setSuggestions(data.products);
          } catch (error) {
            console.log(error);
          }
        };
    
        fetchData();
      }, [value]);
      
    */
    return (
    <div id="search">
        <div class="navbar">
            <span>Home</span>
            <span>Recherche :</span>
        </div>

        <div class="searchbar">
            <h1>Recherche</h1>
            <input type="text" 
              placeholder="Search.."
              value={value}
              onChange={(e) => {
                setValue(e.target.value);
                if(e.target.value===""){
                  setSearchShow(false);
                }
                else {
                  setSearchShow(true);
                }
              }}/>
            <div>
            <Searchlist person={filteredprofil} game={filteredgame}/>
            
              <ul>
                {filteredgame.map((game) => (
                  <li key={game.id}>{game.name} - {game.genre}</li>
                ))}
              </ul>

              
              <ul>
                {filteredprofil.map((profil) => (
                  <li key={profil.id}>{profil.name} - {profil.email}</li>
                ))}
              </ul>
              
            </div>
        </div>
    </div>
    )
}
